// Ecommerce 資料抓取
const puppeteer = require('puppeteer-extra');
const StealthPlugin = require('puppeteer-extra-plugin-stealth');
puppeteer.use(StealthPlugin());

const browser = await puppeteer.launch({ 
  headless: false,
  args: ['--no-sandbox', '--disable-setuid-sandbox']
});

const page = await browser.newPage();

try {
  // 前往 Ecommerce 登入頁面
  await page.goto('https://ecommerce.example.com/admin/login', { waitUntil: 'networkidle2' });

  // 登入
  await page.type('#account', 'admin');
  await page.type('#password', '1234');
  await page.click('.login-btn');
  await page.waitForNavigation();

  // 導航到數據頁面
  await page.click('a[href="/analytics"]');
  await page.waitForSelector('.analytics-data');

  // 抓取數據
  const data = await page.evaluate(() => {
    const metrics = {};

    // 抓取各種指標
    metrics.totalSales = document.querySelector('.total-sales')?.textContent?.trim();
    metrics.orderCount = document.querySelector('.order-count')?.textContent?.trim();
    metrics.avgOrderValue = document.querySelector('.avg-order-value')?.textContent?.trim();
    metrics.conversionRate = document.querySelector('.conversion-rate')?.textContent?.trim();

    return metrics;
  });

  await browser.close();
  return { ecommerceData: data };

} catch (error) {
  await browser.close();
  throw error;
}

